var express = require('express');
var routes = require('./routes');
var user = require('./routes/user');
var http = require('http');
var path = require('path');

var app = express();
app.set('port', process.env.PORT || 3000);
var http = require('http')
    , server = http.createServer(app);

var helpers = require('cloud-htmlhelper')(app);

// all environments

app.set('views', path.join(__dirname, 'views'));
app.set('view engine', 'ejs');
app.use(express.bodyParser());
app.use(express.methodOverride());
app.use(express.static(path.join(__dirname, 'public')));

app.get('/', function(req, res){
    res.render('test', {user: {id: 5, username: 'tim', name: 'Tim'}});
});

app.get('/test', function(req, res){
    res.render('test', {user: {id: 5, username: 'tim', name: 'Tim'}});
});

app.post("/cities", function(req, res){
    var pid = parseInt(req.body.pid);
    var codes = global.sharecode.GetCodesByParentIDSync(31, pid);
    var items = [];
    codes.forEach(function(e,i){
        items.push({Key: e.CodeNameCn, Value: e.CodeID});
    });
    res.send(items);
});

server.listen(app.get('port'));
console.log('Express server listening on port ' + app.get('port'));